import React from 'react'
import '../styles/quiz.css'
import questions from '../data/quizdata'
const Quiz = () => {
    const [CurrentQuestion, setCurrentQuestion] = useState(0)
    const [score, setScore] = useState(0)
    const [CorrectAns, setCorrectAns] = useState(0)
    const [showResult, setShowResult] = useState(false)
    const [clicked, setClicked] = useState(false)
    const handleAnswrOption = (isCorrect) => {
        if (isCorrect) {
            setScore(score + 5)
            setCorrectAns(CorrectAns + 1)

        }
        setClicked(true)
    }
    const handleNextOption = () => {
        const nextQuestion = CurrentQuestion + 1;
        if (nextQuestion < questions.length) {
            setCurrentQuestion(nextQuestion)
        } else {
            setShowResult(true)
        }
    };
    const handlePlayAgain=()=>{
        setCurrentQuestion(0)
        setScore(0)
        setCorrectAns(0)
        setShowResult(false)
    }

    return (
        <>
            <div className='app'>
                {showResult ? (<QuizResult score={score} CorrectAns={CorrectAns} handlePlayAgain = {handlePlayAgain} />) : (<>
                    <div className='question-section'>
                        <h5>Score:{score}</h5>
                        <div className="question-count"></div>
                        <span>Question {currentQuestion + 1} of {questions.length}</span>
                    </div>
                    <div className="question-text">
                        {questions[currentQuestion].question}
                    </div>
                    <div className="answer-section">
                        {questions[CurrentQuestion].options.map((ans, i) => {
                            return  (<button
                                  className={`button ${clicked & ans.isCorrect? "correct":"button"}`}

                                 disabled={clicked} key={i} onCLick={() => handleAnswrOption(ans.isCorrect)}>{ans.answerText}</button>);
                        })}
                        <button>
                            Answers
                        </button>
                        <div className="actions">
                            <button onClick={handlePlayAgain}> Quit </button>
                            <button disabled={!clicked} onClick={handleNextOption}>Next</button>

                        </div>

                    </div>
                </>)}

            </div>
        </>
    )
}
export default Quiz
